mapper.select:

select * from checklist 
where p_no = #{p_no}
order by checklist_no

mapper.update:
	update checklist 
	set check_title = #{check_title}
    where checklist_no = #{checklist_no}

mapper.select:
select * from list
where checklist_no = #{checklist_no}
order by list_no

mapper.update:
update list set
list_info = #{list_info}
where list_no = #{list_no}

update list set
checked = #{checked}
where list_no = #{list_no}
